import React, { useEffect, useState } from 'react';
import './App.css';
import Logo from './components/Logo';
import ServiceStatus from './components/ServiceStatus';
import SubscriptionButton from './components/SubscriptionButton';
import SetupButton from './components/SetupButton';
import ProfileButton from './components/ProfileButton';
import SupportButton from './components/SupportButton';
import SupportModal from './components/SupportModal';
import ProfilePage from './components/ProfilePage';
import SetupFlow from './components/SetupFlow';
import { initTelegramSDK, getTelegramUser } from './services/telegram';
import apiService from './services/api';

function App() {
  const [isSupportModalOpen, setIsSupportModalOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const initializeApp = async () => {
      try {
        setIsLoading(true);

        const telegramData = await initTelegramSDK();
        if (!telegramData) {
          console.warn('Telegram SDK not available');
          setIsLoading(false);
          return;
        }

        const telegramUser = getTelegramUser();
        if (!telegramUser) {
          console.warn('Telegram user data not available');
          setIsLoading(false);
          return;
        }

        try {
          const existingUser = await apiService.getUser(telegramUser.id);
          setUser(existingUser);
        } catch (error) {
          if (error.message.includes('404') || error.message.includes('not found')) {
            try {
              const newUser = await apiService.createUser({
                telegram_id: telegramUser.id,
                first_name: telegramUser.firstName,
                last_name: telegramUser.lastName || '',
                username: telegramUser.username || '',
                language_code: telegramUser.languageCode,
                is_premium: telegramUser.isPremium || false,
              });
              setUser(newUser);
            } catch (createError) {
              console.error('Error creating user:', createError);
            }
          } else {
            console.error('Error fetching user:', error);
          }
        }
      } catch (error) {
        console.error('App initialization error:', error);
      } finally {
        setIsLoading(false);
      }
    };

    initializeApp();
  }, []);

  if (isLoading) {
    return (
      <div className="app">
        <div className="background"></div>
        <div className="content">
          <Logo />
        </div>
      </div>
    );
  }

  if (currentPage === 'profile') {
    return (
      <ProfilePage user={user} onBack={() => setCurrentPage('home')} />
    );
  }

  if (currentPage === 'setup') {
    return (
      <SetupFlow 
        onBack={() => setCurrentPage('home')} 
        onComplete={() => setCurrentPage('home')} 
      />
    );
  }

  return (
      <div className="app">
        <div className="background"></div>
        <div className="content">
          <Logo />
          <div className="buttons-container">
            <ServiceStatus user={user} />
            <SubscriptionButton />
            <SetupButton onOpenSetup={() => setCurrentPage('setup')} />
            <div className="bottom-buttons">
              <ProfileButton onOpenProfile={() => setCurrentPage('profile')} />
              <SupportButton onOpenModal={() => setIsSupportModalOpen(true)} />
            </div>
          </div>
        </div>
        <SupportModal 
          isOpen={isSupportModalOpen} 
          onClose={() => setIsSupportModalOpen(false)} 
        />
      </div>
  );
}

export default App;
