const API_BASE_URL = 'https://api.real-2.space';

class ApiService {
  async request(endpoint, options = {}) {
    const method = options.method || 'GET';
    const url = `${API_BASE_URL}${endpoint}`;

    const headers = {
      'Accept': 'application/json',
      ...options.headers,
    };

    if (method !== 'GET' && method !== 'HEAD') {
      headers['Content-Type'] = 'application/json';
    }

    const config = {
      method: method,
      mode: 'cors',
      credentials: 'omit',
      redirect: 'manual',
      headers: headers,
    };

    if (options.body) {
      if (typeof options.body === 'object') {
        config.body = JSON.stringify(options.body);
      } else {
        config.body = options.body;
      }
    }

    try {
      let response = await fetch(url, config);

      if (response.status === 307 || response.status === 308) {
        const redirectUrl = response.headers.get('Location');
        if (redirectUrl) {
          const redirectConfig = { ...config, redirect: 'follow' };
          response = await fetch(redirectUrl, redirectConfig);
        }
      }

      if (response.status === 0 || response.type === 'opaque') {
        throw new Error('CORS error: Unable to fetch resource');
      }

      let data;
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        data = await response.json();
      } else {
        const text = await response.text();
        try {
          data = JSON.parse(text);
        } catch {
          data = { detail: text };
        }
      }

      if (!response.ok) {
        throw new Error(data.detail || `HTTP error! status: ${response.status}`);
      }

      return data;
    } catch (error) {
      console.error('API request error:', error);
      throw error;
    }
  }

  async createUser(userData) {
    return this.request('/users/', {
      method: 'POST',
      body: userData,
    });
  }

  async getUser(userId) {
    return this.request(`/users/${userId}/`);
  }

  async updateUser(userId, userData) {
    return this.request(`/users/${userId}/`, {
      method: 'PUT',
      body: userData,
    });
  }

  async getSubscription(userId) {
    return this.request(`/users/${userId}/subscription/`);
  }

  async createSubscription(userId, subscriptionData) {
    return this.request(`/users/${userId}/subscription/`, {
      method: 'POST',
      body: subscriptionData,
    });
  }
}

export default new ApiService();
